import Card from "../Card/Card";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

export default function Favorites({id, name, status, species,origin, gender, image, onClose}) {

  const fav = useSelector(state => state.myFavorites);

  return (
    <>
      {fav && fav.map((character) => {
        return (
            <div>
               <Link to={`/detail/${id}`}>
                  <h2>{name}</h2>
               </Link>
               <h2>{status}</h2>
               <h2>{species}</h2>
               <h2>{gender}</h2>
               <h2>{origin}</h2>
               <img src={image} alt='' />
               <button onClick={()=>onClose(id)}>X</button>
            </div>
         )
      })}
    </>
  );
}
