export default function validation(userData, setErrors,errors){
    if(!userData.email){
        setErrors({...errors, email:'Email Vacio'})}

    else{
        if (/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{3})+$/.test(userData.email)){
            setErrors({...errors, email:''})} else setErrors({...errors, email:'Email Invalido'});
        
        if (userData.email.length > 35) setErrors({...errors, email: 'email demasiado largo'});
        }
    
    if(!userData.password){
        setErrors({errors, password:'Ingrese una contraseña'})
    }

    else{
        const regex = /\d+/;
        if(!regex.test(userData.password)){
            setErrors({password:'La contraseña debe tener al menos un numero'})}

        if(userData.password.length<6 || userData.password.length>10){
            setErrors({...errors, password:'la contraseña tiene que tener una longitud entre 6 y 10 caracteres'})
        }
    }

    return errors
}
