import { connect, useDispatch, useSelector } from 'react-redux';
import { addFav, removeFav } from '../../redux/actions';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';


export function Card({id, name, status, species,origin, gender, image, onClose,addFav, removeFav,myFavorites}) {

   const dispatch = useDispatch()
   const [isFav, setIsFav] = useState(false)

   function handleFavorite(){
      if(isFav){
         setIsFav(false);
         removeFav(id);
      }
      else{
         setIsFav(true);
         addFav();
      }
   }

   useEffect(() => {
      myFavorites
      .forEach((fav) => {
         if (fav.id === id) {
            setIsFav(true);
         }
      });
   },[myFavorites]);

   return (
      <div>
         {isFav ? (<button onClick={handleFavorite}>‚ù§Ô∏è</button>) : 
                  (<button onClick={handleFavorite}>ü§ç</button>)
         }
         <Link to={`/detail/${id}`}>
            <h2>{name}</h2>
         </Link>
         <h2>{status}</h2>
         <h2>{species}</h2>
         <h2>{gender}</h2>
         <h2>{origin}</h2>
         <img src={image} alt='' />
         <button onClick={()=>onClose(id)}>X</button>
      </div>
   )
}

export function mapStateToProps(state) {
   return{
      myFavorites: state.myFavorites
   }
}

export function mapDispatchToProps(dispatch) {
   return{
      addFav: (character) => {dispatch(addFav(character))},
      removeFav:(id)=>{dispatch(removeFav(id))}
   }
}

export default connect(mapDispatchToProps, mapStateToProps)(Card)
