export default function validation(userData, setErrors, errors) {
    if (!userData.email) {
      setErrors({...errors, email: 'Email Vacio'});
    } else if (!/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{3})+$/.test(userData.email)) {
      setErrors({...errors, email: 'Email Invalido'});
    } else if (userData.email.length > 35) {
      setErrors({...errors, email: 'email demasiado largo'});
    }
      
    if (!/\d+/.test(userData.password)) {
      setErrors({...errors, password: 'La contrase単a debe tener al menos un numero'});
    } else if (userData.password.length < 6 || userData.password.length > 10) {
      setErrors({...errors, password: 'la contrase単a tiene que tener una longitud entre 6 y 10 caracteres'});
    }
    return errors
  }
